package com.javaex.ex04;

public class Ex07 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// i=1  j->6개
		// i=2  j->6개
		// i=3  j->6개
		// i=4  j->6개
		// i=5  j->6개
		// i=6  j->6개
		
		for(int i=1; i<=6; i++) {
			for(int j=1; j<=6; j++) {
				System.out.print("*");
			}
			System.out.println("");
		}
	}

}
